mapper.select:
SELECT chatroom_id
FROM chatroom
WHERE chatroom_userpk = #{chatroom_userpk}
AND chatroom_friendpk = #{chatroom_friendpk}

mapper.update:
        UPDATE message
		SET message_readstate = #{message_readstate}
		WHERE message_chatroomid = #{message_chatroomid}
        AND message_sendto = #{message_sendto}
