mapper.select:
SELECT count(bookmark_feedpk)
FROM bookmark
WHERE bookmark_feedpk=#{feed_pk} AND bookmark_userpk=#{user_pk}

mapper.delete:
DELETE FROM bookmark
WHERE bookmark_feedpk = #{bookmark_feedpk}
AND bookmark_userpk = #{bookmark_userpk}

